def areYouPlayingBanjo(name):
    return name + " plays banjo" if name[0] == 'r' or name[0] == 'R' else name + " does not play banjo"


def areYouPlayingBanjo1(name):
    return name + (' plays' if name[0].lower() == 'r' else ' does not play') + " banjo"

